from regsyn import globsyn
from nchr import S_
from .repl import joinall
from .pttn import WORDS_TO_SNAKE_WITH_UPPERCASE, SNAKE_PATTERNS

# %% ../nbs/05_snek.ipynb 6
@globsyn({'snake', 'to_snake', 'snakify', 'snakify'})
def snek(s: str) -> str:
    '''Convert a string to snake_case'''
    return joinall(s, WORDS_TO_SNAKE_WITH_UPPERCASE, S_).lower()

    
def py_to_snake(s: str) -> str:    
    '''Convert a string to snake_case without using `re`'''
    _precap = [f'{S_}{c.lower()}' if c.isupper() else c for c in s]
    return ''.join(_precap).lstrip(S_)
